<script lang="ts">import {} from 'svelte';
import Context from './components/Context/Context.svelte';
let { children, ...rest } = $props();
let canvas = $state();
let wrapper = $state();
</script>

<div bind:this={wrapper}>
  <canvas bind:this={canvas}>
    {#if canvas && wrapper}
      <Context
        {wrapper}
        {canvas}
        {...rest}
      >
        {@render children?.()}
      </Context>
    {/if}
  </canvas>
</div>

<style>
  div {
    position: relative;
    width: 100%;
    height: 100%;
  }

  canvas {
    display: block;
    position: relative;
    width: 100%;
    height: 100%;
  }
</style>
